import UIKit

protocol AremWorkerLogic {
    func getData() -> aremDatas
}

class AremWorker: AremWorkerLogic {
     let service: NetworkProtocol
    
     init(service: NetworkProtocol) {
         self.service = service
     }

    // MARK: - Methods
    func getData() -> aremDatas {
        service.createDataFromJson(with: "data")
    }

}
